import mathclass Calculator:    def __init__(self):        self.start_value = 0        # simple addition method    def addition(self, x, y):        solution = x + y        return solution# simple subtraction method    def subtraction(self, x, y):        solution = x - y        return solution    # simple multiplication method    def multiplication(self, x, y):        solution = x * y        return solution    # simple division method    def division(self, x, y):        solution = x / y        return solution    # simple square method    def square(self, x):        solution = x**2        return solution    # not-so-simple square root method    def square_root(self, x):        if x < 0:            return "error"        return math.sqrt(x)    # factorial method (for the plot, we don't really need this, might remove later)    def factorial(self, x):        f = 1        for i in range(1, x+1):            f *= i            return f        # sine method    def sin(self, x):        solution = math.sin(x)        return solution# cos method    def cos(self, x):        solution = math.cos(x)        return solution    # tan method    def tan(self, x):        solution = math.tan(x)        return solution    # arcsine method    def arcsin(self, x):        solution = math.asin(x)        return solution   # arccos method    def arccos(self, x):        solution = math.acos(x)        return solution    # arctan method    def arctan(self, x):        solution = math.atan(x)        return solution            